class ParentCom extends React.Component{
  constructor(props){
    super(props)
    this.state={
      isActive:true
    }
  }
  render(){
    return(
      <div>
        <button>控制子元素显示</button>
        <Childcom isActive={this.state.isActive}/>
      </div>
    )
  }
}

class ChileCom extends React.Component{
  constructor(props){
    super(props)
  }
  render(){
    let strClass =null;
    if(this.props.isActive){
      strClass="active"
    }else{
      strClass=""
    }
    return(
      <div className={"content"+strClass}>
          <h1>我是子元素</h1>
      </div>
    )
  }
}

