import React from 'react';
import './App_content.css';
import img2 from "./deleta.png";
import ReactDOM, { render } from 'react-dom';
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";
import { Button, DatePicker, Select, version, message, Pagination, Avatar, Input, Image, Card, Tabs, List, Space, Radio, Row, Col, Divider } from "antd";
import "antd/dist/antd.css";
import zhCN from 'antd/lib/locale/zh_CN';
import moment from 'moment';
import 'moment/locale/zh-cn';
import { AudioOutlined } from '@ant-design/icons';
import { UserOutlined } from '@ant-design/icons';
import { MessageOutlined, LikeOutlined, StarOutlined } from '@ant-design/icons';
import ListCont from "./ListCont"
const { Meta } = Card;
const { Search } = Input;
const { Option } = Select;
const { TabPane } = Tabs;



//列表组件2
// function ListCont2(props){
class ListCont2 extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      itemList: [],
      itemList1: [],
      titledata: ["所有", "现代", "田园", "欧式", "美式", "中式", "日式", "北欧", "地中海", "东南亚", "简欧", "工业风", "简美", "工装"],
      selectdata: "所有",
      value: 1,
      tit1: "contentb block",
      tit2: "contentb hidden",
      imgurl: "",
      pagenum: 1,
      pagelength: 0,
      itemListpage: 1,
      itemListlimit: 8,
      itemListtotal: 1,
      cartitle_top_img1: "cartitle_top_img block",
      cartitle_top_img2: "cartitle_top_img hidden",
      itemListkeyword: ""
    }

  }

  time = (date) => {
    let json_date = new Date(date).toJSON();
    return new Date(new Date(json_date) + 8 * 3600 * 1000).toISOString().replace(/T/g, ' ').replace(/\.[\d]{3}Z/, '')
  }

  render() {
    return <List.Item>
      <List.Item.Meta
        avatar={
          <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
        }
        avatar={<Avatar src={this.state.itemList1.headimgurl} />}
        title={<a target="_blank" href={'https://bim.zhuxingyun.com/tool/design?id=' + this.props.listcon.id + '&tid=' + this.props.listcon.tid}>{this.props.listcon.name}——{this.time(this.props.listcon.updatedAt)}</a>}
        description={this.props.listcon.properties.comments}
        content={this.time(this.props.listcon.updatedAt)}
        extra={
          <img
            width={272}
            alt="logo"
            src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
          />
        }
      />
    </List.Item>
    // return <div>
    //   <span>uuuuu</span>
    // </div>
  }
}

//主要组件
class Content1 extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      itemList: [],
      itemList1: [],
      titledata: ["所有", "现代", "田园", "欧式", "美式", "中式", "日式", "北欧", "地中海", "东南亚", "简欧", "工业风", "简美", "工装"],
      selectdata: "所有",
      value: 1,
      tit1: "contentb block",
      tit2: "contentb hidden",
      imgurl: "",
      pagenum: 1,
      pagelength: 0,
      itemListpage: 1,
      itemListlimit: 8,
      itemListtotal: 1,
      cartitle_top_img1: "cartitle_top_img block",
      cartitle_top_img2: "cartitle_top_img hidden",
      itemListkeyword: ""
    }

  }

  componentWillMount = () => {
    let _this = this
    window.window.uyun.env = 'prod';
    window.window.uyun.api.authenticateMobileUser('17596576465', 'wangbo1358', function (err, result1) {
      window.window.uyun.api.getDesigns({ limit: _this.state.itemListlimit, page: _this.state.itemListpage }, (err, result) => {
        console.log(result.data)
        console.log(_this.state.itemListlimit)
        _this.setState({
          itemList: result && result.data,
          itemList1: result1,
          itemListpage: result.page,
          itemListtotal: result.total

        })
      })
      // uyun.api.searchPrivateDesigns({keyword:'555'})
      window.window.uyun.util.setToken(result1.token);
    });
  }

  pagecont = (pageNumber) => {
    this.setState({
      itemListpage: pageNumber
    }, () => {
      console.log(this.state.itemListpage);
      console.log(pageNumber);
      let _this = this
      window.window.uyun.env = 'prod';
      window.window.uyun.api.authenticateMobileUser('17596576465', 'wangbo1358', function (err, result1) {
        window.window.uyun.api.getDesigns({ limit: _this.state.itemListlimit, page: _this.state.itemListpage }, (err, result) => {
          console.log(result.data);
          console.log(result1);
          // console.log(result.data.length)
          console.log(_this.state.itemListlimit)
          _this.setState({
            itemList: result && result.data,
            itemList1: result1,
            itemListpage: result.page,
            // itemListlimit: result.limit,
            itemListtotal: result.total

          })
        })
        window.window.uyun.util.setToken(result1.token);
      })
    });
  }
  datacont = (key) => {
    this.setState({
      selectdata: this.state.titledata[key - 1]
    })
  }
  onChange = e => {
    console.log('radio checked', e.target.value);
    this.setState({
      value: e.target.value,
    });
    console.log(this.state.value)
    if (this.state.value == 2) {
      this.setState({
        tit1: "contentb block",
        tit2: "contentb hidden"
      })
    } else {
      this.setState({
        tit1: "contentb hidden",
        tit2: "contentb block"
      })
    }
  };

  suffix = (
    <AudioOutlined
      style={{
        fontSize: 16,
        color: '#1890ff',
      }}
    />
  );
  onSearch = (value) => {
    console.log(value);
    this.setState({
      itemListkeyword: value
    }, () => {
      console.log(this.state.itemListkeyword);
      let _this = this
      window.window.uyun.env = 'prod';
      window.window.uyun.api.authenticateMobileUser('17596576465', 'wangbo1358', function (err, result1) {
        // uyun.api.searchPrivateDesigns({keyword:'555'})
        window.window.uyun.api.searchPrivateDesigns({ keyword: _this.state.itemListkeyword }, (err, result2) => {
          console.log(result2);
          _this.setState({
            itemList: result2 && result2.data,
            itemList1: result1,
            itemListpage: result2.page,
            // itemListlimit: result.limit,
            itemListtotal: result2.total,
            itemListkeyword: result2.keyword
          })
        })
        window.window.uyun.util.setToken(result1.token);
      });
    })
  }



  render() {
    // console.log(this.props)
    return (
      <div className="content1">
        <div className="radio_chang">
          <Radio.Group onChange={this.onChange} value={this.state.value}>
            <Radio value={1}>卡片视图</Radio>
            <Radio value={2}>列表视图</Radio>
          </Radio.Group>
        </div>
        <div className="search_top">
          <Search
            className="searcha"
            placeholder="请输入搜索内容"
            allowClear
            enterButton="搜索"
            size="large"
            onSearch={this.onSearch}
          />
        </div>
        <Tabs className="tabnav" defaultActiveKey="1" onChange={(key) => { this.datacont(key) }}>
          {
            this.state.titledata.map((item, index) => {
              return <TabPane tab={item} key={index + 1} ></TabPane>
            })
          }
        </Tabs>

        <div className={this.state.tit1}>
          {/* 水平，垂直 */}
          <Row
            wrap={true}
            className="rowmar"
            justify="center"
            gutter={[
              // { xs: 8, sm: 16, md: 24, lg: 32 }, 
              // { xs: 8, sm: 16, md: 24, lg: 32 }
              16, 16
            ]}
          >
            {
              this.state.itemList.map((item, index) => {
                console.log(this.state.selectdata)
                if (this.state.selectdata == "所有") {
                  // return ListCont(item,index);
                  return <ListCont key={index} listcon={item} listindex={index} ></ListCont>;
                }
                if (this.state.selectdata === item.properties.style) {
                  // return ListCont(item,index);
                  return <ListCont key={index} listcon={item} listindex={index} ></ListCont>;
                } else {
                  return null;
                }
              })
            }
            <Col span={8} xs={24} sm={24} md={12} lg={8} xxl={6}><li></li></Col>
            <Col span={8} xs={24} sm={24} md={12} lg={8} xxl={6}><li></li></Col>
            <Col span={8} xs={24} sm={24} md={12} lg={8} xxl={6}><li></li></Col>
          </Row>
        </div>
        <div className={this.state.tit2}>
          <List
            itemLayout="horizontal"
            dataSource={this.state.itemList}
            renderItem={item => {
              if (this.state.selectdata == "所有") {
                // return ListCont2(item)
                return <ListCont2 imgurl={this.state.itemList1.headimgurl} listcon={item}></ListCont2>;
              }

              if (this.state.selectdata === item.properties.style) {
                // return ListCont2(item)
                return <ListCont2 imgurl={this.state.itemList1.headimgurl} listcon={item}></ListCont2>;
              } else {
                return null;
              }
            }}
          />
        </div>

        {/* 分页 */}
        <Pagination className="pagecont" defaultCurrent={1} total={this.state.itemListtotal} onChange={(pageNumber) => { this.pagecont(pageNumber) }} />
      </div>
    )
  }
}

export default Content1
